// page init
jQuery(function(){
	jcf.customForms.replaceAll();
	initSlideShow();
	initDropDown();
	initOpenClose();
	initLightbox();
	initSameHeight();
	initAddClasses();
	initTabs();
	jQuery('input, textarea').placeholder();
});

// fade gallery init
function initSlideShow() {
	jQuery('div.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.switcher li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		autoHeight: true,
		tabLinks: 'a'
	});
}

// animated navigation init
function initDropDown() {
	jQuery('#nav ul').animDropdown({
		items: 'li',
		drop: '.drop',
		animSpeed: 400,
		effect: 'fade'
	});
}

// open-close init
function initOpenClose() {
	jQuery('div.open-close').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[data-rel*="lightbox"], input.lightbox').each(function(){
		var link = jQuery(this);
		link.attr('rel', link.attr('data-rel')).fancybox({
			padding: 0,
			cyclic: false,
			overlayShow: true,
			overlayOpacity: 0.65,
			overlayColor: '#000000',
			titlePosition: 'inside',
			onComplete: function(box) {
				if(link.attr('href').indexOf('#') === 0) {
					jQuery('#fancybox-content').find('a.close').unbind('click.fb').bind('click.fb', function(e){
						jQuery.fancybox.close();
						e.preventDefault();
					});
				}
			}
		});
	});
}

/* Fancybox overlay fix */
jQuery(function(){
	// detect device type
	var isTouchDevice = (function() {
		try {
			return ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
		} catch (e) {
			return false;
		}
	}());

	// fix options
	var supportPositionFixed = !( (jQuery.browser.msie && jQuery.browser.version < 8) || isTouchDevice );
	var overlaySelector = '#fancybox-overlay';
	
	if(supportPositionFixed) {
		// create <style> rules
		var head = document.getElementsByTagName('head')[0],
			style = document.createElement('style'),
			rules = document.createTextNode(overlaySelector+'{'+
				'position:fixed;'+
				'top:0;'+
				'left:0;'+
			'}');

		// append style element
		style.type = 'text/css';
		if(style.styleSheet) {
			style.styleSheet.cssText = rules.nodeValue;
		} else {
			style.appendChild(rules);
		}
		head.appendChild(style);
	}
});

// align blocks height
function initSameHeight() {
	jQuery('.news-line').sameHeight({
		elements: '.col-left, .col-right',
		useMinHeight: true,
		flexible: true,
		multiLine: true
	});
}

// add class on click
function initAddClasses() {
	jQuery('.mobile-nav .close').clickClass({
		classAdd: 'nav-active',
		addToParent: true
	});
	jQuery('.expand').clickClass({
		classAdd: 'expanded',
		addToParent: true
	});
}

